#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\brygomez2\Documents\co2_bryan\node_modules\.pnpm\xpress@2.4.6\node_modules\xpress\bin\node_modules;C:\Users\brygomez2\Documents\co2_bryan\node_modules\.pnpm\xpress@2.4.6\node_modules\xpress\node_modules;C:\Users\brygomez2\Documents\co2_bryan\node_modules\.pnpm\xpress@2.4.6\node_modules;C:\Users\brygomez2\Documents\co2_bryan\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/brygomez2/Documents/co2_bryan/node_modules/.pnpm/xpress@2.4.6/node_modules/xpress/bin/node_modules:/mnt/c/Users/brygomez2/Documents/co2_bryan/node_modules/.pnpm/xpress@2.4.6/node_modules/xpress/node_modules:/mnt/c/Users/brygomez2/Documents/co2_bryan/node_modules/.pnpm/xpress@2.4.6/node_modules:/mnt/c/Users/brygomez2/Documents/co2_bryan/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir//usr/local/bin/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir//usr/local/bin/node$exe"  "$basedir/../../bin/init.js" $args
  } else {
    & "$basedir//usr/local/bin/node$exe"  "$basedir/../../bin/init.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "/usr/local/bin/node$exe"  "$basedir/../../bin/init.js" $args
  } else {
    & "/usr/local/bin/node$exe"  "$basedir/../../bin/init.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
